mapper.select:
SELECT A.year, A.mon, C.nm cateName, B.nm, A.standard_score standardScore, A.rating, A.percent FROM mock_result A INNER JOIN subject B ON A.subject_id = B.subject_id INNER JOIN sbj_category C ON C.category_id = B.category_id where A.user_id = #{userId}
AND A.year = #{year}
AND A.mon = #{mon}
ORDER BY A.year desc, A.mon desc, cateName asc

SELECT A.year, A.semester, A.mid_final midFinal, C.nm cateName, B.nm nm,
A.score, A.rating, A.class_rank classRank, A.whole_rank wholeRank
FROM aca_result A
INNER JOIN subject B
ON A.subject_id = B.subject_id
INNER JOIN sbj_category C
ON C.category_id = B.category_id
where A.user_id = #{userId}

    AND A.year = #{year}


    AND A.semester = #{semester}


    AND A.mid_final = #{midFinal}

ORDER BY A.year desc, A.semester desc, A.mid_final desc;
