mapper.select:
    SELECT A.year, A.mon,  B.nm,B.cateName,
    A.standard_score standardScore, A.rating, A.percent
    FROM mock_result A
    INNER JOIN(select C.nm, D.nm cateName
    from subject C
    INNER JOIN sbj_category D
    ON C.category_id = D.category_id
    where type = 1) B
    where A.user_id = #{userId}

        AND A.year = #{year}


    AND A.mon = #{mon}

ORDER BY A.year desc, A.mon desc;
