mapper.select:
SELECT A.result_id resultId, A.`year`, A.semester , A.mid_final mf , B.category_id detailCateId, B.nm detailNm, C.category_id cateId ,C.nm nm , A.score, A.rating , A.class_rank cr, A.whole_rank wr FROM aca_result A INNER JOIN subject B ON B.subject_id = A.subject_id INNER JOIN sbj_category C ON C.category_id = B.category_id WHERE user_id = #{userId}
AND A.year = #{year}
AND A.semester = #{semester}
AND A.mid_final = #{mf}
ORDER BY A.semester DESC , mf DESC

SELECT A.result_id resultId, A.`year`, A.mon, B.category_id detailCateId , B.nm detailNm , C.category_id cateId, C.nm, A.standard_score sc, A.rating , A.percent FROM mock_result A INNER JOIN subject B ON B.subject_id = A.subject_id INNER JOIN sbj_category C ON C.category_id = B.category_id WHERE A.user_id = #{userId}
AND A.year = #{year}
AND A.mon = #{mon}
ORDER BY A.mon desc

mapper.update:
UPDATE user
SET apr_yn = 0
WHERE user_id = #{userId}

UPDATE mock_result
SET year = #{year}
, mon = #{mon}
, standard_score = #{standardScore}
, rating = #{rating}
, percent = #{percent}
, subject_id = #{subjectId}
WHERE result_id = #{resultId}

UPDATE aca_result
SET year = #{year}
, subject_id = #{subjectId}
, semester = #{semester}
, mid_final = #{mf}
, score = #{score}
, rating = #{rating}
, class_rank = #{classRank}
, whole_rank = #{wholeRank}
WHERE result_id = #{resultId}
