mapper.select:
SELECT user_id userId, email, pw, name, role
FROM user
WHERE email = #{email}

select school_id
from school
where nm = #{nm}

select class_id
from van
where year = #{year} and grade = #{grade} and class = #{classNum}

SELECT user_id userId, ip, access_token accessToken, refresh_token refreshToken, created_at createdAt, updated_at updatedAt
FROM user_token
WHERE user_id = #{userId} AND ip = #{ip}
